    private void jTextField1KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextField1KeyReleased
        Pattern p = Pattern.compile("[A-≈ª]+[a-≈º]*");
        String imie = jTextField1.getText();
        Matcher m = p.matcher(imie);
        if(!m.matches() && imie.length()>2) {
            jLabel7.setText("B≈ÇƒÖd w imieniu");
            valid[0] = false;
        } else
            jLabel7.setText("");
        valid[0] = true;
    }//GEN-LAST:event_jTextField1KeyReleased

    private void jTextField3KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextField3KeyReleased
        Pattern p = Pattern.compile("^[a-zA-Z][\\w\\.-]*[a-zA-Z0-9]@[a-zA-Z0-9][\\w\\.-]*[a-zA-Z0-9]\\.[a-zA-Z][a-zA-Z\\.]*[a-zA-Z]$");
        String mail = jTextField3.getText();
        Matcher m = p.matcher(mail);
        //valid[3] = false;
        if(!m.matches()) {
            jLabel11.setText("B≈Çƒôdny adres e-mail");
            valid[3] = false;
        } else
            jLabel11.setText("");
        valid[3] = true;
    }//GEN-LAST:event_jTextField3KeyReleased

    private void jTextField4KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextField4KeyReleased
        Pattern p = Pattern.compile("[0-9]{11}");
        String pesel = jTextField4.getText();
        Matcher m = p.matcher(pesel);
        
        if(m.matches()==false) {
            jLabel10.setText("B≈ÇƒÖd w numerze PESEL");
            valid[2] = false;
        } else {
            char[] peselNr  = pesel.toCharArray();
            int suma = 0;
            int[] mlt = {1, 3, 7, 9, 1, 3, 7, 9, 1, 3};
            for (int i = 0; i < 10; i++) {
                suma+=(peselNr[i]-48)*mlt[i];
            }
            suma %= 10;
            suma = 10-suma;
            if (suma == peselNr[10]-48) {
                jLabel10.setText("");
                valid[2] = true;
            }
        }
    }//GEN-LAST:event_jTextField4KeyReleased
    
